/// <reference types="react" />
declare type TextElement = HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement;
declare type Value = string | number | (string | number)[];
declare type ChangeEventHandler<T extends TextElement> = React.ChangeEventHandler<T>;
interface Options<T extends TextElement> {
    onChange?: ChangeEventHandler<T>;
    value?: Value;
    defaultValue?: Value;
}
/**
 * This is a small hook that will enable a valued flag when the value
 * of a text input or a textarea has a length greater than 0.
 *
 * @private
 */
export default function useValuedState<T extends TextElement>({ onChange, value, defaultValue, }: Options<T>): [boolean, ChangeEventHandler<T> | undefined];
export {};
